function getChessBoard() {
  const el = document.createElement("div");
  el.className = "chess-board";

  return el;
}

function getSlotOnChessBoard(first, last) {
  for (let i = 0; i < 8; i++) {
    slotBoard.push(i % 2 != 0 ? first : last);
  }

  return slotBoard;
}

document.addEventListener("DOMContentLoaded", function () {
  const frame = document.getElementById("frame");
  const chessBoard = getChessBoard();

  // For only test
  // chessBoard.innerHTML = "test";

  for (let i = 0; i < 8; i++) {
    i % 2 != 0
      ? getSlotOnChessBoard("white-slot", "black-slot")
      : getSlotOnChessBoard("black-slot", "white-slot");
  }

  slotBoard.map((item, key) => {
    let slots = document.createElement("div");
    slots.className = item;
    slots.innerHTML = figures[key];

    slots.key = key;
    slots.ondrop = window.utils.getCaptureMouse;
    slots.ondragover = window.utils.allowCapture;

    chessBoard.appendChild(slots);
  });

  frame.appendChild(chessBoard);
});

window.utils = {};

window.utils.allowCapture = (e) => e.preventDefault();

window.utils.captureMouse = function (e) {
  e.dataTransfer.setData("text", e.target.id);
};

window.utils.getCaptureMouse = (e) => {
  let data = document.getElementById(e.dataTransfer.getData("text"));
  let target = e.target;

  if (target.key == undefined) {
    return false;
  }

  e.target.appendChild(data);
};
